from django.contrib.auth.forms import UserCreationFormfrom django.forms import TextInput, PasswordInput, CharField, EmailInputfrom django.contrib.auth.models import Userclass UserCreationForm(UserCreationForm):    password1 = CharField(        widget=PasswordInput(            attrs={'class': 'form-control', 'id': 'Password'}),    )    password2 = CharField(        widget=PasswordInput(            attrs={'class': 'form-control', 'id': 'Password2'}),    )    class Meta:        model = User        fields = ('username', 'email')        widgets = {            'username': TextInput(attrs={'class': 'form-control', 'id': 'username'}),            'email': EmailInput(attrs={'class': 'form-control', 'id': 'email'})        }